import React, { Component } from 'react';
import NavBar from './components/NavBar';
import News from './components/News';
import { 
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import LoadingBar from 'react-top-loading-bar'

export default class App extends Component {
  state = {
    progres:0,
    currmode:"light"
  }
  setProgress = (progres) =>{
    this.setState({progres:progres});
  }
  set =()=> {
    if(this.state.currmode==="light"){
        this.setState({currmode:"dark"});
    } else{
        this.setState({currmode:"light"});
    }
}
render() {
    return (
      <div style={{backgroundColor : "#191510"}}>
        <Router>
        <NavBar mode={currmode} setmode={setmode}/>
        <LoadingBar
        color='#f11946'
        progress={this.state.progres}
        />
        <Routes>
          <Route exact path='/' element={<News  key={'general'} setProgress={this.setProgress} category='general'/>}/>
          <Route exact path='/business' element={<News  key={'business'} setProgress={this.setProgress} category='business'/>}/>
          <Route exact path='/health' element={<News  key={'health'} setProgress={this.setProgress} category='Health'/>}/>
          <Route exact path='/entertainment' element={<News  key={'entertainment'} setProgress={this.setProgress} category='entertainment'/>}/>
          <Route exact path='/general' element={<News  key={'general'} setProgress={this.setProgress} category='general'/>}/>
          <Route exact path='/science' element={<News  key={'science'} setProgress={this.setProgress} category='science'/>}/>
          <Route exact path='/sports' element={<News  key={'sports'} setProgress={this.setProgress} category='sports'/>}/>
          <Route exact path='/technology' element={<News  key={'technology'} setProgress={this.setProgress} category='technology'/>}/>
        </Routes>
        </Router>
      </div>
    )
  }
}
