import React, { Component } from 'react'
import NewsItems from './NewsItems'
import search from './search.gif'
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";



export class News extends Component {
    static defaultProps = {
        country: "in",
        category: "general",
        pagesize: 6
    }
    static propTyps = {
        county: PropTypes.string,
        category: PropTypes.string,
        pagesize: PropTypes.number,
    }
    news = []
    constructor(props) {
        // super();
        this.state = {
            news: this.news,
            loading: true,
            page: 1,
            endpage: 0,
            totalResults: 0
        }
    }

    async componentDidMount() {
        this.setState({ page: 1 });
        this.updatenews();
    }
    handlePrevClick = async () => {
        this.setState({ page: this.state.page - 1 });
        this.updatenews();
    }
    handleNextClick = async () => {
        this.setState({ page: this.state.page + 1 });
        this.updatenews();
    }

    updatenews = async () => {
        this.props.setProgress(10);
        this.setState({ loading: true });
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&page=${this.state.page}&pageSize=${this.props.pagesize}`;
        this.props.setProgress(30);
        let data = await fetch(url);
        let parsedData = await data.json();
        this.props.setProgress(60);
        this.setState({
            page: this.state.page,
            loading: false,
            news: parsedData.articles,
            endpage: Math.ceil(parsedData.totalResults / this.props.pagesize)
        });
        this.props.setProgress(100);
    }
    fetchMoreData = async() =>{
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&page=${this.state.page+1}&pageSize=${this.props.pagesize}`;
        this.props.setProgress(30);
        let data = await fetch(url);
        let parsedData = await data.json();
        this.props.setProgress(60);
        this.setState({
            page: this.state.page + 1,
            loading: false,
            news: this.state.news.concat(parsedData.articles),
        });
        this.props.setProgress(100);
    }

    render() {
        return (
            <>
                <h1 className='text-center' style={{ color: 'white' }}>DailyNews - Top {this.props.category} Headlines</h1>
                {/* {this.state.loading && <div className="text-center">
                    <img src={search} alt="" />
                </div>} */}
                <InfiniteScroll
                    dataLength={this.state.news.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.news.length != this.state.totalResults}
                    loader={<h1 className='text-center my-3'><img src={search}/></h1>}
                >
                    <div className="container">
                    <div className="row">
                        {this.state.news.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItems title={element.title} description={element.description} imgurl={element.urlToImage} url={element.url} author={element.author} time={element.publishedAt} />
                            </div>
                        })}
                    </div>
                    </div>
                </InfiniteScroll>
                {/* <div className="container d-flex justify-content-between">
                    <button disabled={this.state.page <= 1} type="button" className="btn btn-secondary" onClick={this.handlePrevClick}>&larr; Previous</button>
                    <button disabled={this.state.page >= this.state.endpage} type="button" className="btn btn-primary" onClick={this.handleNextClick}>Next &rarr;</button>
                </div> */}
            </>
        )
    }
}

export default News
