import { useState } from "react";
import Theme from "./Theme";

const ThemeProvider = (props) => {
    const [theme, setTheme] = useState("light")
    
    return (
        <Theme.Provider value={{theme, setTheme}}>
            {props.children}
        </Theme.Provider>
    )
}

export default The
