import React, { Component } from 'react'
import NewsItems from './NewsItems'
import search from './search.gif'
import PropTypes from 'prop-types'



export class News extends Component {
    static defaultProps = {
        country : "in",
        category : "science",
        pagesize : 6
    }
    static propTyps = {
        county : PropTypes.string,
        category : PropTypes.string,
        pagesize : PropTypes.number,
    }
    news = []
    constructor(){
        super();
        this.state = {
            news: this.news, 
            loading: true, 
            page: 1,
            endpage: 0
        }
    }

    async componentDidMount(){
        let ur = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&q=news&page=1&pageSize=${this.props.pagesize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            endpage:Math.ceil(parsedData.totalResults/this.props.pagesize)
        });
        this.updatenews(0);
        console.log(this.state.endpage);
    }

    updatenews = async(num) => {
        this.setState({loading:true});
        const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&q=news&page=${this.state.page+num}&pageSize=${this.props.pagesize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            page:this.state.page+num,
            loading:false,
            news:parsedData.articles
        });
        if(num==0){
            this.setState()
        }
    }
    handlePrevClick = async() => {
        
    }
    handleNextClick = async() => {
        this.setState({loading:true});
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&q=news&page=${this.state.page+1}&pageSize=${this.props.pagesize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            page:this.state.page+1,
            loading:false,
            news:parsedData.articles
        });
    }

    render() {
        return (
            <div className='container my-3'>
                <h1 className='text-center'>DailyNews - Top Headlines</h1>
                {this.state.loading && <div className="text-center">
                    <img src={search} alt="" />
                </div>}
                <div className="row">
                {!this.state.loading && this.state.news.map((element)=>{
                    return <div className="col-md-4" key={element.url}>
                        <NewsItems title={element.title} description={element.description} imgurl={element.urlToImage} url={element.url} author={element.author} time={element.publishedAt}/>
                    </div>
                })}  
                </div>
                <div className="container d-flex justify-content-between">
                    <button disabled={this.state.page<=1} type="button" className="btn btn-secondary" onClick={this.updatenews(-1)}>&larr; Previous</button>
                    <button disabled={this.state.page>=this.state.endpage} type="button" className="btn btn-primary" onClick={this.updatenews(1)}>Next &rarr;</button>
                </div>
            </div>
        )
    }
}

export default News
