import React, { Component } from 'react'
import NewsItems from './NewsItems'
import search from './search.gif'
import PropTypes from 'prop-types'



export class News extends Component {
    static defaultProps = {
        country: "in",
        category: "general",
        pagesize: 6
    }
    static propTyps = {
        county: PropTypes.string,
        category: PropTypes.string,
        pagesize: PropTypes.number,
    }
    news = []
    constructor() {
        super();
        this.state = {
            news: this.news,
            loading: true,
            page: 1,
            endpage: 0,
            totalResults 
        }
    }

    async componentDidMount() {
        this.setState({ page: 1 });
        this.updatenews();
    }
    handlePrevClick = async () => {
        this.setState({ page: this.state.page - 1 });
        this.updatenews();
    }
    handleNextClick = async () => {
        this.setState({ page: this.state.page + 1 });
        this.updatenews();
    }

    updatenews = async () => {
        this.props.setProgress(10);
        this.setState({ loading: true });
        const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&page=${this.state.page}&pageSize=${this.props.pagesize}`;
        this.props.setProgress(30);
        let data = await fetch(url);
        let parsedData = await data.json();
        this.props.setProgress(60);
        if (this.endpage == 0) {
            this.setState({});
            console.log(this.endpage);
        }
        this.props.setProgress(80);
        this.setState({
            page: this.state.page,
            loading: false,
            news: parsedData.articles,
            endpage: Math.ceil(parsedData.totalResults / this.props.pagesize)
        });
        this.props.setProgress(100);
    }

    render() {
        return (
            <div className='container my-3'>
                <h1 className='text-center' style={{ color: 'white' }}>DailyNews - Top {this.props.category} Headlines</h1>
                {/* {this.state.loading && <div className="text-center">
                    <img src={search} alt="" />
                </div>} */}
                <InfiniteScroll
                    dataLength={this.state.articles.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.articles.length !== this.totalResults}
                    loader={<h4>Loading...</h4>}
                >
                    <div className="row">
                        {this.state.news.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItems title={element.title} description={element.description} imgurl={element.urlToImage} url={element.url} author={element.author} time={element.publishedAt} />
                            </div>
                        })}
                    </div>
                </InfiniteScroll>
                <div className="container d-flex justify-content-between">
                    <button disabled={this.state.page <= 1} type="button" className="btn btn-secondary" onClick={this.handlePrevClick}>&larr; Previous</button>
                    <button disabled={this.state.page >= this.state.endpage} type="button" className="btn btn-primary" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </div>
        )
    }
}

export default News
