import React, { useState, useEffect } from 'react'
import NewsItems from './NewsItems'
import search from './search.gif'
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";
// import Theme from '../context/Theme';


const News = (props) => {
    const [news, setNews] = useState([])
    const [loading, setLoading] = useState(true)
    const [totalResults, setTotalResults] = useState(0)
    const [page, setPage] = useState(1)

    const getTitle = (title) => {
        return title.charAt(0).toUpperCase() + title.slice(1);
    }

    // const updatenews = async () => {
    //     props.setProgress(10)
    //     setLoading(true)
    //     let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page}&pageSize=${props.pagesize}`
    //     props.setProgress(30)
    //     let data = await fetch(url)
    //     let parsedData = await data.json()
    //     props.setProgress(60)
    //     setTotalResults(parsedData.totalResults)
    //     setPage(page)
    //     setLoading(false)
    //     setNews(parsedData.articles)
    //     props.setProgress(100)
    // }

    useEffect(()=>{
        document.title = `${getTitle(props.category)} - DailyNews`
        fetchMoreData()
    },[])


    const fetchMoreData = async () => {
        props.setProgress(10)
        setLoading(true)
        let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page + 1}&pageSize=${props.pagesize}`
        props.setProgress(30)
        let data = await fetch(url)
        let parsedData = await data.json()
        props.setProgress(60)
        setTotalResults(parsedData.totalResults)
        setPage(page + 1)
        setLoading(false)
        setNews(news.concat(parsedData.articles))
        props.setProgress(100)
        console.log(news.length)
    }

    return (
        <>
            <h2 className='text-center mt-5' style={{ fontFamily: 'serif'}}>DailyNews - Top {getTitle(props.category)} Headlines</h2>
            <InfiniteScroll
                dataLength={news.length}
                next={fetchMoreData}
                hasMore={news.length !== totalResults}
                loader={<h4 className='text-center'><img src={search} alt='loading..' /></h4>}
            >
                <div className="container my-1">
                    <div className="row">
                        {news.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItems title={element.title} description={element.description} imgurl={element.urlToImage} url={element.url} author={element.author} time={element.publishedAt} source={element.source.name} />
                            </div>
                        })}
                    </div>
                </div>
            </InfiniteScroll>
        </>
    )

}
News.defaultProps = {
    country: "in",
    category: "general",
    pagesize: 6
}
News.propTyps = {
    county: PropTypes.string,
    category: PropTypes.string,
    pagesize: PropTypes.number,
    apiKey: PropTypes.string
}

export default News
