import React, { Component } from 'react'
import NewsItems from './NewsItems'
import search from './search.gif'
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";



export class News extends Component {
    static defaultProps = {
        country: "in",
        category: "general",
        pagesize: 6
    }
    static propTyps = {
        county: PropTypes.string,
        category: PropTypes.string,
        pagesize: PropTypes.number,
    }
    news = []
    constructor(props) {
        super(props);
        this.state = {
            news: [],
            loading: true,
            page: 1,
            totalResults: 0
        }
        document.title = `${this.getTitle(this.props.category)} - DailyNews`
    }

    getTitle = (title) => {
        return title.charAt(0).toUpperCase() + title.slice(1);
    }

    async componentDidMount() {
        this.updatenews();
    }

    updatenews = async () => {
        this.props.setProgress(10);
        this.setState({ loading: true });
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&page=${this.state.page}&pageSize=${this.props.pagesize}`;
        this.props.setProgress(30);
        let data = await fetch(url);
        let parsedData = await data.json();
        this.props.setProgress(60);
        this.setState({
            page: this.state.page,
            loading: false,
            news: parsedData.articles,
        });
        this.props.setProgress(100);
    }
    fetchMoreData = async () => {
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=c1751d841ec44860916dbd73c430d278&page=${this.state.page + 1}&pageSize=${this.props.pagesize}`;
        this.props.setProgress(30);
        let data = await fetch(url);
        let parsedData = await data.json();
        this.props.setProgress(60);
        this.setState({
            page: this.state.page + 1,
            loading: false,
            news: this.state.news.concat(parsedData.articles),
        });
        this.props.setProgress(100);
        console.log(this.state.totalResults);
    }

    render() {
        return (
            <>
                <h1 className='text-center'>DailyNews - Top {this.getTitle(this.props.category)} Headlines</h1>
                <InfiniteScroll
                    dataLength={this.state.news.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.news.length != this.state.totalResults}
                    loader={false && <h4 className='text-center'><img src={search} /></h4>}
                >
                    <div className="container my-3">
                        <div className="row">
                            {this.state.news.map((element) => {
                                return <div className="col-md-4" key={element.url}>
                                    <NewsItems title={element.title} description={element.description} imgurl={element.urlToImage} url={element.url} author={element.author} time={element.publishedAt} source={element.source.name} />
                                </div>
                            })}
                        </div>
                    </div>
                </InfiniteScroll>
            </>
        )
    }
}

export default News
