import React, { Component } from 'react'


export class NewsItems extends Component {
    

    render() {
        let {title, description, imgurl, url, author, time} = this.props;
        let defaultimg = "https://source.unsplash.com/random/900%C3%97700/?news";
        return (
            <div className='my-3'>
                <div className="card">
                    <img src={imgurl?imgurl:defaultimg} className="card-img-top" alt=""/>
                        <div className="card-body">
                            <h5 className="card-title">{title}</h5>
                            <p className="card-text">{description}</p>
                            <p class="card-text"><small class="text-body-secondary">Last Updated on {new Date(time).toGMTString()}</small></p>
                            <p class="card-text"><small class="text-body-secondary">By {author?author:"Unknown"}</small></p>
                            <a href={url} className="btn btn-primary" target='_blank' rel='noreferrer'>Read More</a>
                        </div>
                </div>
            </div>
        )
    }
}

export default NewsItems
