import React, { Component } from 'react'
import {
    Link
} from "react-router-dom";
import light from "./light.png";
import dark from "./dark.png";

export class NavBar extends Component {
    state = {
        currmode : "light",
    }
    setmode =()=> {
        if(this.state.currmode==="light"){
            this.setState({currmode:"dark"});
        } else{
            this.setState({currmode:"light"});
        }
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg bg-body-tertiary" data-bs-theme={this.state.currmode}>
                    <div className="container-fluid">
                        <Link className="navbar-brand" to="/">DailyNews</Link>
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                                <li className="nav-item">
                                    <Link className="nav-link active" aria-current="page" to="/">Home</Link>
                                </li>
                                <li className="nav-item"><Link className="nav-link" to="/general">General</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/health">Health</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/entertainment">Entertainment</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/science">Science</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/sports">Sports</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/technology">Technology</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/business">Business</Link></li>
                            </ul>
                        </div>
                        {/* for dark mode on off */}
                        <div className="d-flex align-items-center">
                            <img src={this.state.currmode==="light"?dark:light} style={{width:"35px", height : "35px"}} onClick={this.setmode} al/>
                        </div>
                    </div>
                </nav>
            </div>
        )
    }
}

export default NavBar
