import React, { useState, useEffect } from 'react'
import NewsItems from './NewsItems'
import search from './search.gif'
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";



const News = (props) => {
    const [news, setNews] = useState([])
    const [loading, setLoading] = useState(true)
    const [totalResults, setTotalResults] = useState(0)
    const [page, setPage] = useState(1)
    // document.title = `${this.getTitle(props.category)} - DailyNews`

    const getTitle = (title) => {
        return title.charAt(0).toUpperCase() + title.slice(1);
    }

    const updatenews = async () => {
        props.setProgress(10);
        this.setState({ loading: true });
        let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${this.state.page}&pageSize=${props.pagesize}`;
        props.setProgress(30);
        let data = await fetch(url);
        let parsedData = await data.json();
        props.setProgress(60);
        this.setState({
            totalResults: parsedData.totalResults,
            page: this.state.page,
            loading: false,
            news: parsedData.articles,
        });
        props.setProgress(100);
    }

    useEffect(()=>{
        this.updatenews()
    }, [])


    fetchMoreData = async () => {
        let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${this.state.page + 1}&pageSize=${props.pagesize}`;
        props.setProgress(30);
        let data = await fetch(url);
        let parsedData = await data.json();
        props.setProgress(60);
        setPage(page + 1)
        setLoading(false)
        setNews(parsedData.articles)
        props.setProgress(100);
        console.log(this.state.news.length);
    }

    return (
        <>
            <h1 className='text-center'>DailyNews - Top {this.getTitle(props.category)} Headlines</h1>
            <InfiniteScroll
                dataLength={this.state.news.length}
                next={this.fetchMoreData}
                hasMore={this.state.news.length !== this.state.totalResults}
                loader={<h4 className='text-center'><img src={search} alt='loading..' /></h4>}
            >
                <div className="container my-3">
                    <div className="row">
                        {this.state.news.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItems title={element.title} description={element.description} imgurl={element.urlToImage} url={element.url} author={element.author} time={element.publishedAt} source={element.source.name} />
                            </div>
                        })}
                    </div>
                </div>
            </InfiniteScroll>
        </>
    )

}
News.defaultProps = {
    country: "in",
    category: "general",
    pagesize: 6
}
News.propTyps = {
    county: PropTypes.string,
    category: PropTypes.string,
    pagesize: PropTypes.number,
    apiKey: PropTypes.string
}

export default News
